#!/usr/bin/env perl

my @descriptions = <DATA>;

my $default_description = qq'    "description": "",\n';

open DEX, '<', 'pokedex.json';
foreach (<DEX>) {
	print;
	if (/generation/) {
		$single_description = shift @descriptions;
		print $single_description || $default_description;
	}
}
close DEX;

